import axios from "axios";
class MovieDataService {
  getAll(page = 0) {
    return axios.get(`http://localhost:3000/api/v1/movies?page=${page}`);
  }
  get(id) {
    return axios.get(`http://localhost:3000/api/v1/movies/id/${id}`);
  }
  find(query, by = "title", page = 0) {
    return axios.get(
      `http://localhost:3000/api/v1/movies?${by}=${query}&page=${page}`
    );
  }
  createReview(data) {
    return axios.post("http://localhsot:3000/api/v1/movies/review", data);
  }
  updateReview(data) {
    return axios.put("http://localhsot:3000/api/v1/movies/review", data);
  }
  deleteReview(id, userId) {
    return axios.delete("http://localhost:3000/api/v1/movies/review", {
      data: { review_id: id, user_id: userId },
    });
  }
  getRatings() {
    return axios.get("https://localhost:3000/api/v1/movies/ratings");
  }
}
export default MovieDataService;
